/**
 *  动态加载css或者js文件
 * 
 *  @author Vincent(zhongyongsheng@chinaduo.com)
 * 	@date 2011-12-19
 * 	@version 1.0.0
 */
;(function($){
	$.uiwidget = $.uiwidget || {};
	$.uiwidget.Include = function(cfg){
		cfg = $.extend({}, cfg);
		$.extend(this, cfg);
		this.include(this.files);    
	};
	
	$.uiwidget.Include.prototype = {
		include: function(file)
	    {
	        var files = typeof file == "string" ? [file] : file;
	        for (var i = 0; i < files.length; i++)
	        {
	            //var name = files[i].replace(/^\s|\s$/g, "");
	            //var att = name.split('.');
	            //var ext = att[att.length - 1].toLowerCase();
	            var isCSS = /\.css/.test(files[i]);
	            if(isCSS){
        		    var styleTag = document.createElement("link");
        		    styleTag.setAttribute('type', 'text/css');
        		    styleTag.setAttribute('rel', 'stylesheet');
        		    styleTag.setAttribute('href', files[i]);
        		    $("head")[0].appendChild(styleTag);
	            }else{
	            	var script = document.createElement('script');
	            	script.type = "text/javascript";
	            	script.language="javascript";
	            	script.src = files[i];
	            	$("head")[0].appendChild(script);
	            }
	        }
	    }	
	};
	
	$.include = function(cfg){
		return new $.uiwidget.Include(cfg);
	};
})(jQuery);	